// ========== SEED FUNCTION ==========
async function seed() {
  console.log(' Starting GrantLink database seed...\n')

  // 1. Focus Areas
  console.log(` Inserting ${FOCUS_AREAS.length} focus areas...`)
  const { error: faError } = await supabase
    .from('focus_areas')
    .upsert(FOCUS_AREAS, { onConflict: 'id' })
  if (faError) {
    console.error('   Focus areas error:', faError.message)
    process.exit(1)
  }
  console.log('   Focus areas done')

  // 2. Funders
  console.log(` Inserting ${FUNDERS.length} funders...`)
  const { error: fError } = await supabase
    .from('funders')
    .upsert(FUNDERS, { onConflict: 'id' })
  if (fError) {
    console.error('   Funders error:', fError.message)
    process.exit(1)
  }
  console.log('   Funders done')

  // 3. Opportunities (insert in batches of 20 to avoid payload limits)
  console.log(` Inserting ${OPPORTUNITIES.length} opportunities...`)
  const batchSize = 20
  for (let i = 0; i < OPPORTUNITIES.length; i += batchSize) {
    const batch = OPPORTUNITIES.slice(i, i + batchSize)
    const { error: oError } = await supabase
      .from('opportunities')
      .upsert(batch, { onConflict: 'id' })
    if (oError) {
      console.error(`   Opportunities batch ${i / batchSize + 1} error:`, oError.message)
      process.exit(1)
    }
    console.log(`   Batch ${Math.floor(i / batchSize) + 1}/${Math.ceil(OPPORTUNITIES.length / batchSize)} done`)
  }
  console.log('   All opportunities done')

  // 4. Junction table
  console.log(` Inserting ${OPPORTUNITY_FOCUS_AREAS.length} focus area links...`)
  // Delete existing junction data first to avoid duplicates
  await supabase.from('opportunity_focus_areas').delete().neq('opportunity_id', '00000000-0000-0000-0000-000000000000')

  for (let i = 0; i < OPPORTUNITY_FOCUS_AREAS.length; i += batchSize) {
    const batch = OPPORTUNITY_FOCUS_AREAS.slice(i, i + batchSize)
    const { error: jError } = await supabase
      .from('opportunity_focus_areas')
      .upsert(batch, { onConflict: 'opportunity_id,focus_area_id' })
    if (jError) {
      console.error(`   Junction batch ${i / batchSize + 1} error:`, jError.message)
      process.exit(1)
    }
  }
  console.log('   Focus area links done')

  // Verify counts
  const [faCount, fCount, oCount, jCount] = await Promise.all([
    supabase.from('focus_areas').select('id', { count: 'exact', head: true }),
    supabase.from('funders').select('id', { count: 'exact', head: true }),
    supabase.from('opportunities').select('id', { count: 'exact', head: true }),
    supabase.from('opportunity_focus_areas').select('opportunity_id', { count: 'exact', head: true }),
  ])

  console.log('\n Final counts:')
  console.log(`  Focus Areas: ${faCount.count}`)
  console.log(`  Funders: ${fCount.count}`)
  console.log(`  Opportunities: ${oCount.count}`)
  console.log(`  Focus Area Links: ${jCount.count}`)
  console.log('\n Seed complete!')
}

seed().catch((err) => {
  console.error('Fatal error:', err)
  process.exit(1)
})
